import { useNavigate } from "react-router-dom";
import * as tacoService from '../services/tacoService'
import '../css/CreatePage.css'
import React, { useState } from 'react';
import tacos from '../images/tacos.png'

export default function TacoCreate(){

    const [selectedTortilla, setSelectedTortilla] = useState('');
    const [tortillaImage, setTortillaImage] = useState('');

    const [meat, setMeat] = useState('');
    const [meatImg, setMeatImg] = useState('')


    const meatHandler = (event) => {
        const selected = event.target.value

        setMeat(selected)
        updateMeat(selected)
    }

    const updateMeat = (type) => {

        let imgs;

        switch (type) {
            case 'beef':
                imgs = 'data:image/jpeg;base64,'
                break;
                case 'pork':
                    imgs = 'https://media.istockphoto.com/id/515273289/photo/turkish-doner-kebab.jpg?s=612x612&w=0&k=20&c=LczoOqG4GhYBWNO0OJeLm8FSmnEmxjGlNUUBmeUbmFw='
                    break;
                    case 'beef':
                        imgs = 'data:image/jpeg;base64,'
                        break;
                        case 'chicken':
                            imgs = 'https://res.cloudinary.com/perkchops/image/upload/v1629396558/ingredients/2021719/hlcwp2hips3mea9by7l4.png'
                            break;
                            case 'fish':
                                imgs = 'https://img.freepik.com/premium-photo/canned-tuna-white-surface_55883-1349.jpg'
        
            default:
                break;
        }
        setMeatImg(imgs)
    }

    const handleTortillaChange = (event) => {
        const selectedValue = event.target.value;

        // Set the selected tortilla type and update the image accordingly
        setSelectedTortilla(selectedValue);
        updateTortillaImage(selectedValue);
    };

    

    const updateTortillaImage = (tortillaType) => {
        // Use a switch statement or any logic to determine the image source based on tortilla type
        let imageSource;

        switch (tortillaType) {
            case 'white':
                imageSource = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQL1V9X2NPB0hyAln4otL1Sjape65grwKtFcQ&usqp=CAU';
                break;
            case 'brown':
                imageSource = 'data:image/jpeg;base64,';
                break;
            case 'corn':
                imageSource = 'https://www.jessicagavin.com/wp-content/uploads/2020/04/corn-tortillas-22-1200.jpg';
                break;
            default:
                // Default image or placeholder if no specific value is selected
                imageSource = tacos;
                
        }

        setTortillaImage(imageSource);
    };


    const createSubmitHandler = async (e) => {
        e.preventDefault()

        const data = Object.fromEntries(new FormData(e.currentTarget))

          try {
            await tacoService.create(data)
            navigate('/tacos')
            
            console.log(data);
          } catch (error) {
            console.log(error);
          }

    }

    const navigate = useNavigate()


    return(


        <section id="create-page" className="auth">
        <form id="create" onSubmit={createSubmitHandler}>
            <div className="containers">
                <h1>Create your own Taco</h1>

                <label htmlFor="leg-title" >Custom</label>
                
                <label htmlFor="bread">Tortilla Bread Type</label>
                <select id="bread" name="bread" required onChange={handleTortillaChange} >
                <option value='' hidden></option>

                    <option value="white" >White Tortilla</option>
                    <option value="brown">Brown Tortilla with seeds</option>
                    <option value="corn">Corn Bread Tortilla</option>
                    <option value="rpg"></option>
                </select>
                {selectedTortilla && (
            <div>
              <img src={tortillaImage} alt={selectedTortilla} />
            </div>



          )}



                <label htmlFor="meats">Meat</label>
                <select id="meats" name="meats" required onChange={meatHandler} >
                <option value='' hidden></option>

                    <option value="beef">Beef</option>
                    <option value="pork">Pork</option>
                    <option value="chicken">Chicken</option>
                    <option value="fish">Tuna fish</option>
                    {/* Add more options based on your specific categories */}
                </select>
                {meat && (
            <div>
              <img src={meatImg} alt={meat} />
            </div>



          )}
                
    
                <label htmlFor="salads">Salads</label>
                <select id="salads" required name="salads">
                <option value='' hidden></option>

                    <option value="beans, tomatoes and corn">Tomato, Beans, Corn</option>
                    <option value="rice, avocado and cabbage">Avocado, Rice, Cabbage</option>
                    <option value="potato, beets and carrots">Beets, Potatoes, Carrot </option>
                </select>
    
              
    
                <label htmlFor="cheese">Cheese</label>
                <select id="cheese" required name="cheese">
                <option value='' hidden></option>

                    <option value="cheddar">Cheddar</option>
                    <option value="mozzarella">Mozzarella</option>
                </select>

                <label htmlFor="desc">Add More:</label>
                    <textarea name="desc" id="desc" ></textarea>


                <input className="btn submit" type="submit" value="Create Taco" />
            </div>
        </form>
    </section>
    
    )
}